import altair as altimport pandas as pd#from vega_datasets import datadf = data.seattle_weather()df.head()plot1= alt.Chart(df).mark_tick().encode(x='precipitation',)plot1.save('altair.html')#load datafridge=pd.read_csv("eco/sum/2012-06-27.csv")#generate time series datatime=pd.timedelta_range(0, periods=86400, freq='S')time1=pd.Series(time)time_all1=time1.to_frame()for i in range(86400):    time_all1[i,0]=str(time_all1.iloc[i,0])[-8:]    #combine time and consumptionsframe1=[time_all1,fridge]result=pd.concat(frame1,axis=1)#melt dataresult1=result.melt(id_vars='Integers',var_name='household',value_name='consumption')##plotfridge1=fridge.melt(var_name='household',value_name='consumption')seconds=pd.Series(range(1,259200))seconds.name='seconds'frame2=[fridge1,seconds]fridge2=pd.concat(frame2,axis=1)plot2= alt.Chart(fridge2).mark_tick().encode(x='seconds',y='consumption',color='household')plot2.save('altair.html')